savecontent variable="mailBody" { 
 writeOutput("Your Email Message!!"); 
 }; 
 // Create and populate the mail object 
 mailService = new mail( 
 to = "your@email.com", 
 from = "another@email.com", 
 subject = "CFMail Example", 
 body = mailBody 
 ); 
 // Send 
 mailService.send();
